import React, { useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const Popular = () => {
    const [courses] = useState([
        {
            id: 1,
            // price: '$200',
            review: '4.5',
            reviewCount: '40',
            duration: '6.5',
            lessons: '97',
            img: 'assets/images/course/img_01.jpg',
            title: 'SQL-Data Analysis: Crash Course',
        },
        {
            id: 2,
            price: '$200',
            review: '4.5',
            reviewCount: '40',
            duration: '6.5',
            lessons: '97',
            img: 'assets/images/course/img_01.jpg',
            title: 'Learn How to Start an Amazon FBA Store',
        },
        {
            id: 3,
            price: '$450',
            review: '4.5',
            reviewCount: '40',
            duration: '6.5',
            lessons: '97',
            img: 'assets/images/course/img_01.jpg',
            title: 'Emotional Intelligence at Work: Learn Emotions',
        },
        {
            id: 4,
            price: '$250',
            review: '4.5',
            reviewCount: '40',
            duration: '6.5',
            lessons: '97',
            img: 'assets/images/course/img_01.jpg',
            title: 'Competitive Strategy Law & Organization',
        },
        {
            id: 5,
            price: '$300',
            review: '4.7',
            reviewCount: '35',
            duration: '5.0',
            lessons: '85',
            img: 'assets/images/course/img_02.jpg',
            title: 'Mastering Python for Data Science',
        },
        {
            id: 6,
            price: '$150',
            review: '4.6',
            reviewCount: '50',
            duration: '4.5',
            lessons: '60',
            img: 'assets/images/course/img_03.jpg',
            title: 'Beginnerâ€™s Guide to Web Development',
        },
        {
            id: 7,
            price: '$100',
            review: '4.3',
            reviewCount: '20',
            duration: '3.5',
            lessons: '45',
            img: 'assets/images/course/img_04.jpg',
            title: 'Introduction to Digital Marketing',
        }
    ]);

    // Slick slider settings for autoplay and slow movement
    const settings = {
        dots: true,           // Show dots navigation at the bottom
        infinite: true,       // Infinite scrolling
        speed: 2000,          // Transition speed between slides (in milliseconds)
        slidesToShow: 3,      // Show 3 slides at a time
        slidesToScroll: 1,    // Scroll 1 slide at a time
        autoplay: true,       // Enable autoplay
        autoplaySpeed: 3000,  // Delay between each slide (in milliseconds)
        pauseOnHover: false   // Keep autoplay running even on hover
    };

    return (
        <section className="py-10 bg-gray-100">
            <div className="container mx-auto px-5">
                <div className="text-center mb-10">
                    <h2 className="text-3xl font-bold">Popular Courses</h2>
                    <p className="text-gray-600">Discover Your Perfect Program In Our Courses.</p>
                </div>
                <Slider {...settings}>
                    {courses.map((course) => (
                        <div key={course.id} className="p-2">
                            <div className="bg-white rounded-lg shadow-lg hover:shadow-xl transition-shadow">
                                <div className="relative">
                                    <img src={course.img} alt={course.title} className="w-full h-48 object-cover rounded-t-lg" />
                                    <div className="absolute top-2 right-2 bg-blue-500 text-white px-3 py-1 rounded-lg">
                                        {course.price}
                                    </div>
                                </div>
                                <div className="p-5">
                                    <div className="flex items-center mb-2">
                                        <div className="text-yellow-400">
                                            {[...Array(5)].map((star, i) => (
                                                <i key={i} className={`fa fa-star ${i < Math.floor(course.review) ? '' : 'text-gray-300'}`}></i>
                                            ))}
                                        </div>
                                        <span className="ml-2 text-sm text-gray-600">{course.review} ({course.reviewCount} reviews)</span>
                                    </div>
                                    <h3 className="text-lg font-semibold mb-3">{course.title}</h3>
                                    <div className="flex justify-between text-gray-600">
                                        <span><i className="far fa-clock"></i> {course.duration} hr</span>
                                        <span><i className="far fa-play-circle"></i> {course.lessons} Lessons</span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </Slider>
            </div>
        </section>
    );
};

export default Popular;






















