import React from 'react';

const Popular = () => {
  const courses = [
    {
      title: "Building A Better World One Student At A Time",
      lectures: 58,
      level: "Beginner",
      students: 243,
      image: "path_to_image_1.jpg",
    },
    {
      title: "Growing Together to Create Lifelong Learners",
      lectures: 85,
      level: "Intermediate",
      students: 450,
      image: "path_to_image_2.jpg",
    },
    {
      title: "Education is About Preparing Students for College",
      lectures: 35,
      level: "Intermediate",
      students: 135,
      image: "path_to_image_3.jpg",
    },
  ];

  return (
    <section className="py-12 bg-white">
      <div className="container mx-auto">
        <h2 className="text-2xl font-semibold mb-8">Courses You May Like</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {courses.map((course, index) => (
            <div
              key={index}
              className="border rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300"
            >
              <img
                src={course.image}
                alt={course.title}
                className="w-full h-48 object-cover"
              />
              <div className="p-4">
                <div className="text-sm text-gray-500 flex items-center space-x-2 mb-2">
                  <span>{course.lectures} Lectures</span>
                  <span className="text-gray-400">â€¢</span>
                  <span>{course.level}</span>
                </div>
                <h3 className="text-lg font-semibold mb-2">{course.title}</h3>
                <div className="flex items-center text-sm text-gray-500">
                  <span className="flex items-center mr-2">
                    <i className="ri-user-line"></i> {course.students} Students
                  </span>
                  <span className="ml-auto text-indigo-600">
                    <i className="ri-arrow-right-line"></i>
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Popular;
