import React, { useState, useEffect } from 'react';

const Nav = () => {
  const [menuItems, setMenuItems] = useState([
    {
      label: 'Home',
      link: '/',
    },
    {
      label: 'About',
      link: '/about',
    },
    {
      label: 'Courses',
      link: '/course-list',
      submenu: [
        { label: 'Course 1', link: '/course-1' },
        { label: 'Course 2', link: '/course-2' },
        { label: 'Course 3', link: '/course-3' },
      ],
      submenuOpen: false,
    },
    {
      label: 'Discover',
      link: '#',
      submenu: [
        { label: 'Blog', link: '/blog' },
        { label: 'Teachers', link: '/teachers' },
        { label: 'Testimonials', link: '/testimonials' },
        { label: 'Career', link: '/career' },
        { label: 'FAQ', link: '/faq' },
      ],
      submenuOpen: false,
    },
    {
      label: 'Contact',
      link: '/contact',
    },
  ]);

  // Toggle submenu open/close state
  const handleSubmenuToggle = (index) => {
    setMenuItems((prevMenuItems) =>
      prevMenuItems.map((item, i) =>
        i === index ? { ...item, submenuOpen: !item.submenuOpen } : item
      )
    );
  };

  return (
    <ul className="primary-menu">
      {menuItems.map((item, index) => (
        <li key={index}>
          <a href={item.link}>{item.label}</a>

          {/* If the item has a submenu, show the trigger */}
          {item.submenu && (
            <>
              <span
                className={`menu-trigger ${item.submenuOpen ? 'open' : ''}`}
                onClick={() => handleSubmenuToggle(index)}
              >
                <i className={`submenu-icon fal ${item.submenuOpen ? 'fa-angle-up' : 'fa-angle-down'}`}></i>
              </span>
              {/* Submenu */}
              <ul className={`submenu ${item.submenuOpen ? 'open' : ''}`}>
                {item.submenu.map((subitem, subindex) => (
                  <li key={subindex}>
                    <a href={subitem.link}>{subitem.label}</a>
                  </li>
                ))}
              </ul>
            </>
          )}
        </li>
      ))}
    </ul>
  );
};

export default Nav;
