import React from "react";
import "./Intro.css"; // Make sure to import your CSS file


const Intro = () => {
  return (
    <div className="text-black py-12 px-6">
      {/* Main container */}
      <div className="max-w-7xl mx-auto flex flex-col md:flex-row items-center">
        {/* Left side - images */}


        {/* Right side - text content */}
        <div className="md:w-1/2 mt-8 md:mt-0 md:ml-12">
          <h2 className="text-4xl font-bold text-black">
          	Narrated  <span className="text-red-500">Abu Hurairah </span>  that Messenger of Allah{" "}
            <span className="text-red-500">(ﷺ)</span><span> said</span>
          </h2>
          <p className="text-gray-900 mt-4">
          "Whoever takes a path upon which to obtain knowledge, Allah makes the path to Paradise easy for him." 
          </p>

          <p className="mt-1 font-semibold text-black">
          <span className="text-red-500">- Jami` at-Tirmidhi 2646</span> 
          </p>

          {/* Bullet points */}
          {/* <ul className="mt-4 space-y-2">
            <li className="flex items-center">
              <span className="text-red-500 mr-2">✔</span> We take pride in fostering strong,
              lasting relationships
            </li>
            <li className="flex items-center">
              <span className="text-red-500 mr-2">✔</span> Delivering high-quality, tailored
              learning experience
            </li>
            <li className="flex items-center">
              <span className="text-red-500 mr-2">✔</span> Focus on traditional knowledge
            </li>
          </ul> */}
          <p>At Bayyinah Academy, we offer high-quality online Quran and Arabic language education course to learners of  all ages. Our qualified and experience tutors are committed to helping you achieve fluency and a deep understanding of both the  Quran and the Arabic language. With the guidance of our dedicated native tutor, you'll unlock the beauty of the Quran while mastering Arabic. Join us on this journey of spiritual and linguistic growth!</p>

          {/* Button */}
          {/* <div className="mt-8">
            <a
              href="#"
              className="bg-red-500 text-white px-6 py-3 rounded-full hover:bg-red-600 transition"
            >
              Learn More
            </a>
          </div> */}
        </div>
      </div>
    </div>
  );
};

export default Intro;
