import React from 'react';

const Popular = () => {

    // Sample course data with reviews
    const courseData = [
        {
            "id": 1,
            "title": "Sample Course",
            "reviews": [
                {
                    "userName": "John Doe",
                    "userImage": "assets/images/blog/author.jpg",
                    "rating": 4.5,
                    "text": "Great course! I learned a lot and highly recommend it."
                },
                {
                    "userName": "Jane Smith",
                    "userImage": "assets/images/blog/author2.jpg",
                    "rating": 5,
                    "text": "Amazing content and well-structured lessons."
                }
            ]
        },
        {
            "id": 2,
            "title": "Another Course",
            "reviews": [
                {
                    "userName": "Michael Brown",
                    "userImage": "assets/images/blog/author3.jpg",
                    "rating": 4,
                    "text": "Very informative and well-taught."
                }
            ]
        }
    ];

    // Assuming we want to display reviews for the first course in the list
    const courseItem = courseData[0]; // Change this index to pick another course

    return (
        <>
            <div>Popular</div>
            <section className="popular-courses page-wrapper">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-12">
                            <div className="course-tab-content">
                                <div id="course-reviews">
                                    <ul className="course-reviews-list">
                                        {/* Mapping through the reviews of the course */}
                                        {courseItem.reviews.map((review, index) => (
                                            <li key={index}>
                                                <div className="course-review">
                                                    <div className="course-single-review">
                                                        <div className="user-image">
                                                            <img 
                                                                src={review.userImage ? review.userImage : 'assets/images/blog/author.jpg'} 
                                                                alt="User" 
                                                                className="img-fluid"
                                                            />
                                                        </div>

                                                        <div className="user-content user-review-content">
                                                            <div className="review-header mb-10">
                                                                <h4 className="user-name">{review.userName}</h4>

                                                                <div className="rating review-stars-rated">
                                                                    {/* Displaying star rating */}
                                                                    {Array.from({ length: 5 }, (_, i) => (
                                                                        <a key={i} href="#">
                                                                            <i className={i < review.rating ? 'fa fa-star' : 'fa fa-star-half'}></i>
                                                                        </a>
                                                                    ))}
                                                                </div>
                                                            </div>

                                                            <div className="review-text">
                                                                <div className="review-content">
                                                                    {review.text}                               
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </li>
                                        ))}
                                    </ul>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </>
    );
};

export default Popular;
