import React, { useState } from 'react';

const Faq = () => {
  const [activeIndex, setActiveIndex] = useState(null);

  const faqData = [
    {
      question: "Is the class schedule suitable for me?",
      answer: "Yes, our class schedule is flexible, allowing you to choose times that work best for you."
    },
    {
      question: "How do I begin?",
      answer: "You can begin by registering an account with us and selecting the courses you are interested in."
    },
    {
      question: "Are these classes pre-recorded?",
      answer: "No, our classes are conducted live to provide the best interaction between students and instructors."
    },
    {
      question: "Where is the headquarter of your business?",
      answer: "Our headquarter is located in New York City, USA."
    }
  ];

  const toggleAccordion = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  return (
    <section
      className="faq-section py-12 text-white bg-cover bg-center"
      style={{
        backgroundImage: 'url(/path/to/your/background-image.jpg)', // Update with the path to your image
      }}
    >
      <div className="container mx-auto px-4 bg-blue-900 bg-opacity-90 p-8 rounded-lg">
        <div className="text-center mb-12">
          <span className="text-red-500 font-semibold uppercase">Process</span>
          <h2 className="text-3xl font-bold mt-2">
            Frequently Asked <span className="text-red-500">Questions</span>
          </h2>
          <p className="text-gray-300 mt-2">
            Appropriately enhance principle-centered innovation rather than high standards in platforms. Credibly orchestrate functional.
          </p>
        </div>

        <div className="space-y-4 max-w-2xl mx-auto">
          {faqData.map((item, index) => (
            <div
              key={index}
              className={`p-4 rounded-lg ${
                activeIndex === index ? 'bg-blue-800 bg-opacity-60' : 'bg-transparent'
              } transition-colors duration-300`}>
              <button
                className="flex items-center justify-between w-full text-left focus:outline-none text-lg font-semibold text-white"
                onClick={() => toggleAccordion(index)}
              >
                <span>{item.question}</span>
                <span className="text-2xl">{activeIndex === index ? '-' : '+'}</span>
              </button>
              {activeIndex === index && (
                <div className="mt-2 text-sm text-gray-300">
                  {item.answer}
                </div>
              )}
            </div>
          ))}
        </div>

        <div className="text-center mt-8">
          <button className="bg-red-500 text-white py-2 px-4 rounded-lg font-semibold hover:bg-red-600 transition-colors">
            More About
          </button>
        </div>
      </div>
    </section>
  );
};

export default Faq;
